// --- Firebase (module) ---
import { initializeApp } from "https://www.gstatic.com/firebasejs/9.6.10/firebase-app.js";
import {
    getAuth,
    RecaptchaVerifier,
    signInWithPhoneNumber,
} from "https://www.gstatic.com/firebasejs/9.6.10/firebase-auth.js";

// --- Firebase Initialization (single init) ---
const firebaseConfig = {
    apiKey: "AIzaSyAQQVDajaCdDDhL_bfBqJOl4b0zBgBSLic",
    authDomain: "dor-real-estate.firebaseapp.com", // <-- fix this key
    projectId: "dor-real-estate",
    storageBucket: "dor-real-estate.appspot.com",
    messagingSenderId: "501082745824",
    appId: "1:501082745824:web:cda8afd68c1d8148b45985",
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
window.firebaseApp = app;
window.firebaseAuth = auth;

// --- Helper: Basic IL phone normalization to E.164 ---
function formatILPhoneE164(raw) {
    const t = (raw || "").trim();
    if (!t) return "";
    if (t.startsWith("+")) return t;
    return t.replace(/^\s*0/, "+972");
}

// --- Phone Auth Setup (called when register step is rendered) ---
let recaptchaWidgetId = null;

function setupPhoneAuth() {
  const host = ensureRecaptchaRoot();         // <— use persistent host
  if (!window.recaptchaVerifier) {
      window.recaptchaVerifier = new RecaptchaVerifier(
        host,                        // container ID or element for captcha
        { size: 'invisible' },       // options
        auth                         // Firebase Auth instance
      );
    window.recaptchaVerifier.render().then(id => recaptchaWidgetId = id);
  }

  let confirmationResult = null;
  const sendBtn   = document.getElementById('sendCodeBtn');
  const verifyBtn = document.getElementById('verifyCodeBtn');
  if (!sendBtn || !verifyBtn) return;

  sendBtn.onclick = async () => {
    const phoneRaw = (document.getElementById('phoneNumber')?.value || '').trim();
    const fullName = (document.getElementById('fullName')?.value || '').trim();
    if (!fullName) return alert('אנא הזינו שם מלא');
    if (!phoneRaw) return alert('אנא הזינו מספר טלפון');

    if (typeof grecaptcha !== 'undefined' && recaptchaWidgetId != null) {
      grecaptcha.reset(recaptchaWidgetId);     // re-arm for retries
    }

    const phone = formatILPhoneE164(phoneRaw);
    sendBtn.disabled = true;
    try {
      confirmationResult = await signInWithPhoneNumber(auth, phone, window.recaptchaVerifier);
      document.getElementById('smsStep')?.style && (document.getElementById('smsStep').style.display = 'block');
      document.getElementById('smsCode')?.focus();
      alert('קוד נשלח לנייד');
    } catch (err) {
      alert('בעיה בשליחת SMS: ' + err.message);
    } finally {
      sendBtn.disabled = false;
    }
  };

    verifyBtn.onclick = async () => {
      if (!confirmationResult) return alert('שלחו קוד קודם (לחצן "שלחו לי קוד אימות")');
      const code = (document.getElementById('smsCode')?.value || '').trim();
      if (!code) return alert('אנא הזינו את הקוד');

      try {
        const cred = await confirmationResult.confirm(code);

        userData.auth = {
          uid: cred.user.uid,
          phone: cred.user.phoneNumber || (document.getElementById('phoneNumber')?.value || '').trim(),
          name: (document.getElementById('fullName')?.value || '').trim()
        };
        saveUserData();

        alert('אומת בהצלחה!');
        // >>> go to the questionnaire (step type: "quick-qs")
        goToStepType('quick-qs');       // keeps the onboarding open
        // recaptcha widget will be torn down by your loadStep() logic
      } catch (e) {
        alert('קוד שגוי');
        console.warn(e);
      }
    };
}

// Persistent, off-screen host for reCAPTCHA (created once)
function ensureRecaptchaRoot() {
  let el = document.getElementById('recaptcha-root');
  if (!el) {
    el = document.createElement('div');
    el.id = 'recaptcha-root';
    // keep it in the DOM (required) but out of sight
    el.style.position = 'fixed';
    el.style.left = '-9999px';
    el.style.bottom = '0';
    document.body.appendChild(el);
  }
  return el;
}

// DOM Elements
const modalOverlay = document.getElementById("modalOverlay");
const modalContent = document.getElementById("modalContent");
const modalClose = document.getElementById("modalClose");
const onboardingOverlay = document.getElementById("onboardingOverlay");
const onboardingContent = document.getElementById("onboardingContent");
const onboardingClose = document.getElementById("onboardingClose");
const progressFill = document.getElementById("progressFill");
const progressText = document.getElementById("progressText");
const prevBtn = document.getElementById("prevBtn");
const nextBtn = document.getElementById("nextBtn");
const startJourneyBtn = document.getElementById("startJourney");
const swipeInterface = document.getElementById("swipeInterface");

// State Management
