function setupSubmenus() {
    const toggles = document.querySelectorAll(".main-nav .has-submenu > a");

    function closeAll() {
        document
            .querySelectorAll(".main-nav .has-submenu.open")
            .forEach((li) => li.classList.remove("open"));
    }

    toggles.forEach((a) => {
        a.addEventListener("click", function (e) {
            e.preventDefault();
            const li = a.parentElement;
            const wasOpen = li.classList.contains("open");
            closeAll();
            if (!wasOpen) li.classList.add("open");
        });
    });

    document.addEventListener("click", function (e) {
        if (!e.target.closest(".main-nav")) closeAll();
    });

    document.addEventListener("keydown", function (e) {
        if (e.key === "Escape") closeAll();
    });
}

// === Accessibility Toolbar (A11Y) ===
function initAccessibilityToolbar() {
    const toolbar = document.getElementById("a11y-toolbar");
    if (!toolbar) return; // אין טולבר בדף

    const btnToggle = document.getElementById("a11y-toggle-btn");
    const panel = document.getElementById("a11y-panel");
    const links = () => Array.from(panel.querySelectorAll(".a11y-link"));
    const body = document.body;
    const root = document.documentElement; // עובדים על html
    const STORAGE_KEY = "a11y-state-v1";

    const FONT_STEPS = [
        "a11y-font-120",
        "a11y-font-130",
        "a11y-font-140",
        "a11y-font-150",
        "a11y-font-160",
        "a11y-font-170",
        "a11y-font-180",
        "a11y-font-190",
        "a11y-font-200",
    ];
    let fontIndex = 0; // 0 ללא שינוי

    function openPanel() {
        toolbar.classList.add("a11y-open");
        panel.hidden = false;
        btnToggle.setAttribute("aria-expanded", "true");
        links().forEach((b) => b.setAttribute("tabindex", "0"));
    }

    function closePanel() {
        toolbar.classList.remove("a11y-open");
        panel.hidden = true;
        btnToggle.setAttribute("aria-expanded", "false");
        links().forEach((b) => b.setAttribute("tabindex", "-1"));
    }

  function togglePanel() {
        toolbar.classList.contains("a11y-open") ? closePanel() : openPanel();
    }

    btnToggle.addEventListener("click", (e) => {
        e.preventDefault();
        togglePanel();
    });
    btnToggle.addEventListener("keyup", (e) => {
        if (e.key === "Tab") openPanel();
    });

    function clearFontClasses() {
        FONT_STEPS.forEach((c) => root.classList.remove(c));
    }
    function applyFont() {
        clearFontClasses();
        if (fontIndex > 0) root.classList.add(FONT_STEPS[fontIndex - 1]);
    }
    function setToggleClass(cls) {
        body.classList.toggle(cls);
    }
    function resetAll() {
        [
            "a11y-grayscale",
            "a11y-high-contrast",
            "a11y-negative",
            "a11y-light-bg",
            "a11y-underline",
            "a11y-readable",
        ].forEach((c) => body.classList.remove(c));
        clearFontClasses();
        fontIndex = 0;
        saveState();
    }

    function saveState() {
        const state = {
            fontIndex,
            classes: [
                "a11y-grayscale",
                "a11y-high-contrast",
                "a11y-negative",
                "a11y-light-bg",
                "a11y-underline",
                "a11y-readable",
            ].filter((c) => body.classList.contains(c)),
        };
        localStorage.setItem(STORAGE_KEY, JSON.stringify(state));
    }

    function restoreState() {
        const raw = localStorage.getItem(STORAGE_KEY);
        if (!raw) return;
        try {
            const s = JSON.parse(raw);
            fontIndex = Math.min(
                Math.max(Number(s.fontIndex || 0), 0),
                FONT_STEPS.length,
            );
            applyFont();
            (s.classes || []).forEach((c) => body.classList.add(c));
        } catch {}
    }

    panel.addEventListener("click", (e) => {
        const el = e.target.closest("button[data-action]");
        if (!el) return;
        const action = el.getAttribute("data-action");
        switch (action) {
            case "font-plus":
                fontIndex = Math.min(fontIndex + 1, FONT_STEPS.length);
                applyFont();
                break;
            case "font-minus":
                fontIndex = Math.max(fontIndex - 1, 0);
                applyFont();
                break;
            case "grayscale":
                setToggleClass("a11y-grayscale");
                break;
            case "high-contrast":
                setToggleClass("a11y-high-contrast");
                break;
            case "negative":
                setToggleClass("a11y-negative");
                break;
            case "light-bg":
                setToggleClass("a11y-light-bg");
                break;
            case "underline":
                setToggleClass("a11y-underline");
                break;
            case "readable":
                setToggleClass("a11y-readable");
                break;
            case "reset":
                resetAll();
                break;
        }
        saveState();
    });

    // delegate clicks anywhere on the page
    document.addEventListener("click", (e) => {
        const btn = e.target.closest("[data-open-contact]");
        if (!btn) return;
        openModal("contact");
    });

    document.addEventListener("keydown", (e) => {
        if (e.key === "Escape" && toolbar.classList.contains("a11y-open")) {
            closePanel();
            btnToggle.focus();
        }
    });

    // התחלה
    restoreState();
    if (panel.hidden) links().forEach((b) => b.setAttribute("tabindex", "-1"));
}

function initAboutTimeline() {
    const list = document.getElementById("aboutTlList");
    if (!list) return;

    const step = () => Math.round(list.clientWidth * 0.8);

    const rail = list.closest(".about-tl-rail");
    const btnPrev = rail.querySelector(".about-tl-nav.prev");
    const btnNext = rail.querySelector(".about-tl-nav.next");

    btnPrev.addEventListener("click", () =>
        list.scrollBy({ left: -step(), behavior: "smooth" }),
    );
    btnNext.addEventListener("click", () =>
        list.scrollBy({ left: step(), behavior: "smooth" }),
    );

    // מקשי מקלדת
    list.addEventListener("keydown", (e) => {
        if (e.key === "ArrowLeft")
            list.scrollBy({ left: step(), behavior: "smooth" });
        if (e.key === "ArrowRight")
            list.scrollBy({ left: -step(), behavior: "smooth" });
    });
}

function initServicesTimeline() {
    const rail = document.getElementById("servicesTlList");
    if (!rail) return;
    const wrap = rail.closest(".services-tl-rail");
    const prev = wrap.querySelector(".services-tl-nav.prev");
    const next = wrap.querySelector(".services-tl-nav.next");

    const itemWidth = () => {
        const it = rail.querySelector(".svc-item");
        const gap = parseFloat(
            getComputedStyle(rail).columnGap ||
                getComputedStyle(rail).gap ||
                16,
        );
        return (it?.offsetWidth || 280) + gap;
    };

    const by = (dir) =>
        rail.scrollBy({ left: dir * itemWidth(), behavior: "smooth" });

    prev.addEventListener("click", () => by(-1));
    next.addEventListener("click", () => by(1));

    const updateBtns = () => {
        const max = rail.scrollWidth - rail.clientWidth - 1;
        prev.disabled = rail.scrollLeft <= 0;
        next.disabled = rail.scrollLeft >= max;
    };
    rail.addEventListener("scroll", updateBtns);
    window.addEventListener("resize", updateBtns);
    setTimeout(updateBtns, 0);

    // נגיעה/גרירה (נוח לנייד)
    let down = false,
        sx = 0,
        sl = 0;
    const start = (x) => {
        down = true;
        sx = x;
        sl = rail.scrollLeft;
    };
    const move = (x) => {
        if (down) rail.scrollLeft = sl - (x - sx);
    };
    const end = () => {
        down = false;
    };

    rail.addEventListener("mousedown", (e) => start(e.clientX));
    window.addEventListener("mousemove", (e) => move(e.clientX));
    window.addEventListener("mouseup", end);
    rail.addEventListener("touchstart", (e) => start(e.touches[0].clientX), {
        passive: true,
    });
    rail.addEventListener("touchmove", (e) => move(e.touches[0].clientX), {
        passive: true,
    });
    rail.addEventListener("touchend", end);
}

function startPropertySwiping() {
    currentPropertyIndex = 0;
    likedProperties = [];
    swipeInterface.style.display = "block";
    loadProperty();

    const likeBtn = document.getElementById("likeBtn");
    const dislikeBtn = document.getElementById("dislikeBtn");
    if (likeBtn) likeBtn.onclick = () => swipeProperty("like");
    if (dislikeBtn) dislikeBtn.onclick = () => swipeProperty("dislike");
}

// make inline HTML handlers work in module mode
window.switchExecutive = switchExecutive;
// bottom of script_fixed.js
// one global, no confusion
window.contactAdvisor = () => openModal("contact");

// Map Hebrew UI -> DB slugs
function hebTypeToSlug(t) {
  const map = {
    "דירה": "apartment",
    "דירת גן": "garden_apartment",
    "פנטהאוז": "penthouse",
    "בית פרטי": "villa",
    "דירת סטודיו": "studio"
  };
  return map[t] || "";
}

function hebAreaToSlug(a) {
  const map = {
    "תל אביב": "tel-aviv",
    "ירושלים": "jerusalem",
    "גוש דן": "gush-dan",
    "חיפה והצפון": "north",
    "באר שבע והדרום": "south"
  };
  return map[a] || "";
}

function initAssetsModal(scope) {
  const typeSel = scope.querySelector('#assetTypeSelect');
  const areaSel = scope.querySelector('#assetAreaSelect');
  const minEl   = scope.querySelector('#minPriceInput');
  const maxEl   = scope.querySelector('#maxPriceInput');
  const btn     = scope.querySelector('#assetsSearchBtn');
  const results = scope.querySelector('#assetsResults');
  if (!btn || !results) return;

  // singleton DB
  window.__dorDB = window.__dorDB || new PropertyDatabase();
  const db = window.__dorDB;

  function fmtNIS(n) {
    if (n == null) return '';
    try { return Number(n).toLocaleString('he-IL'); } catch { return n; }
  }

  function render(list) {
    if (!list || list.length === 0) {
      results.innerHTML = `
        <div style="text-align:center; color:var(--text-secondary); padding:1.5rem;">
          <i class="fas fa-search" style="font-size:2rem; opacity:.7;"></i>
          <div style="margin-top:.5rem;">לא נמצאו נכסים תואמים. נסו להרחיב את החיפוש.</div>
        </div>`;
      return;
    }
    results.innerHTML = `
      <div class="assets-grid" style="display:grid; grid-template-columns: repeat(auto-fill, minmax(260px,1fr)); gap: 1rem;">
        ${list.map(p => `
          <article class="asset-card" style="border:1px solid #eee; border-radius:14px; overflow:hidden;">
            <div style="background:#f8f9fa; height:150px; display:flex; align-items:center; justify-content:center;">
              <i class="${p.image || 'fas fa-building'}" style="font-size:2rem;"></i>
            </div>
            <div style="padding:1rem;">
              <h3 style="margin:0 0 .25rem 0; font-size:1.05rem;">${p.title || ''}</h3>
              <div style="color:var(--text-secondary); font-size:.95rem; margin-bottom:.5rem;">
                ${p.neighborhood ? p.neighborhood + " · " : ""}${p.area || ""}
              </div>
              <div style="font-weight:700; color:var(--primary-color);">${fmtNIS(p.price)} ₪</div>
              <div style="font-size:.9rem; color:#666; margin-top:.25rem;">
                ${p.rooms || '-'} חד׳ · ${p.sqm || '-'} מ״ר · ${p.floor != null ? 'קומה ' + p.floor : ''}
              </div>
              <button class="cta-button" style="margin-top:.75rem; width:100%; border-radius:10px;"
                      onclick="window.contactAdvisor && window.contactAdvisor()">
                מעוניין/ת
              </button>
            </div>
          </article>
        `).join('')}
      </div>`;
  }

  function search() {
    const filters = {};
    const t = typeSel?.value?.trim();
    const a = areaSel?.value?.trim();
    const min = Number(minEl?.value || '');
    const max = Number(maxEl?.value || '');

    if (t) filters.type = hebTypeToSlug(t);
    if (a) filters.area = hebAreaToSlug(a);
    if (!Number.isNaN(min) && min > 0) filters.minPrice = min;
    if (!Number.isNaN(max) && max > 0) filters.maxPrice = max;

    const list = db.getProperties(filters);
    render(list.length ? list : db.getTrendingProperties(12));
  }

  // First paint: trending
  render(db.getTrendingProperties(12));

  // Bind
  btn.addEventListener('click', search);
  // optional: live search on change
  typeSel && typeSel.addEventListener('change', search);
  areaSel && areaSel.addEventListener('change', search);
}

styles.css.content:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --primary-color: #d4af37;
    --secondary-color: #2c3e50;
    --text-primary: #1a1a1a;
    --text-secondary: #1a1a1a;
    --text-light: #f8f9fa;
    --bg-light: #f8f9fa;
    --bg-dark: rgba(0, 0, 0, 0.8);
    --border-light: rgba(255, 255, 255, 0.2);
    --transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --shadow-light: 0 2px 20px rgba(0, 0, 0, 0.1);
    --shadow-heavy: 0 10px 40px rgba(0, 0, 0, 0.2);
}

html, body {
    height: 100%;
    overflow-x: hidden;
    background-image: url('gen_pic/bg_blurred.png');
    background-size: cover;
    background-position: center center;
    background-repeat: no-repeat;
    background-attachment: fixed;
    font-family: 'Heebo', 'Inter', sans-serif;
    direction: rtl;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-tap-highlight-color: transparent;
}

body {
    display: flex;
    flex-direction: column;
}

/* Touch optimization for mobile */
@media (pointer: coarse) {
    button, a, .category-card, .option-card {
        min-height: 44px;
        min-width: 44px;
    }

    /* Better touch targets for navigation */
    .nav-grid a {
        padding: 0.5rem;
        border-radius: 8px;
        transition: background-color 0.2s ease;
    }

    .nav-grid a:active {
        background-color: rgba(212, 175, 55, 0.2);
    }

    /* Better touch targets for buttons */
    .cta-button {
        min-height: 48px;
        touch-action: manipulation;
    }

    .social-link {
        min-width: 48px;
        min-height: 48px;
        touch-action: manipulation;
    }
}


/* TOP BAR STARTS HERE — FINAL */

.container {
  max-width: 100%;
  margin: 0;
  padding: 0 2rem;
  width: 100%;
}

/* Mobile container adjustments */
@media (max-width: 768px) {
  .container {
    padding: 0 1.5rem;
  }
}

@media (max-width: 480px) {
  .container {
    padding: 0 1rem;
  }
}

.top-bar {
  height: 10vh;
  min-height: 64px;
  backdrop-filter: none;
  background-color: rgba(255, 180, 51, 0.05);
  border-bottom: none;
  position: relative;
  z-index: 100;
  width: 100%;
}

/* Center the nav block within a tasteful frame */
.top-bar .container {
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 1220px;        /* adjust 1120–1320 to taste */
  margin-inline: auto;      /* true horizontal centering */
  padding-inline: 2rem;     /* equal side insets */
  transform: translateX(4rem);          /* no manual shifts */
}

/* --- NAV CORE --- */
.main-nav {
  width: max-content;       /* shrink-wrap to content so it centers perfectly */
  margin-inline: auto;
}

/* 3 items + logo + 3 items, equal gaps */
.main-nav > ul.nav-grid {
  --nav-gap: 4rem;        /* master spacing between neighbors */
  display: grid;
  grid-auto-flow: column;
  grid-template-columns: repeat(7, max-content);
  align-items: center;
  justify-content: center;
  column-gap: var(--nav-gap);
  list-style: none;
  margin: 0;
  padding: 0;
  width: auto;
}

/* Logo cell: no extra clickable area */
.logo-cell { justify-self: center; width: max-content; }
.logo-link { display: inline-block; line-height: 0; }
.logo { height: 4rem; width: auto; display: block; } /* tune size here */

/* Top-level links */
.main-nav > ul.nav-grid > li > a {
  text-decoration: none;
  color: var(--text-light);
  font-weight: 500;
  transition: var(--transition);
  padding: 0.5rem 0;
  position: relative;
  white-space: nowrap;
}

/* No underline on the logo */
.logo-cell > .logo-link::after,
.logo-cell > .logo-link:hover::after {
  content: none !important;
}

/* Underline effect only for non-logo items */
.main-nav > ul.nav-grid > li:not(.logo-cell) > a { display: inline-block; white-space: nowrap; }
.main-nav > ul.nav-grid > li:not(.logo-cell) > a::after {
  content: '';
  position: absolute;
  inset-inline-end: 0;
  inset-block-end: 0;
  width: 0;
  height: 2px;
  background: var(--primary-color);
  transition: var(--transition);
}
.main-nav > ul.nav-grid > li:not(.logo-cell) > a:hover { color: var(--primary-color); }
.main-nav > ul.nav-grid > li:not(.logo-cell) > a:hover::after { width: 100%; }

/* Disable any dropdowns for MVP */
.main-nav ul.submenu,
.has-submenu.open > ul.submenu { display: none !important; }
@media (hover:hover) and (pointer:fine) {
  .has-submenu:hover > ul.submenu { display: none !important; }
}

/* Keep generic link rule simple/safe */
.main-nav a { text-decoration: none; }

/* Remove login area in MVP */
.user-actions, .login-btn { display: none !important; }

/* Mobile Navigation Optimization */
@media (max-width: 900px) {
  .main-nav > ul.nav-grid { 
    --nav-gap: 1.5rem; 
    row-gap: 0.5rem;
    font-size: 0.9rem;
  }
  .logo { height: 3rem; }

  /* Hero adjustments */
  .hero-title {
    font-size: 1.8rem !important;
    line-height: 1.3;
    margin-bottom: 1rem;
  }

  .hero-subtitle {
    font-size: 1rem !important;
    margin-bottom: 1.5rem;
  }

  .cta-button {
    padding: 0.8rem 2rem;
    font-size: 1rem;
  }
}

@media (max-width: 680px) {
  .main-nav { width: 100%; }
  .main-nav > ul.nav-grid {
    grid-template-columns: repeat(3, 1fr);
    justify-items: center;
    column-gap: 0.5rem;
    row-gap: 0.3rem;
    width: 100%;
    font-size: 0.8rem;
  }

  .logo-cell { grid-column: 2 / span 1; grid-row: 1; }
  .nav-grid > li:nth-child(1),
  .nav-grid > li:nth-child(2),
  .nav-grid > li:nth-child(3) { grid-row: 1; }
  .nav-grid > li:nth-child(5),
  .nav-grid > li:nth-child(6),
  .nav-grid > li:nth-child(7) { grid-row: 2; }

  .logo { height: 2.5rem; }

  /* Hero mobile optimization */
  .hero-section {
    padding: 1rem;
  }

  .hero-title {
    font-size: 1.5rem !important;
    line-height: 1.2;
    margin-bottom: 0.8rem;
  }

  .hero-subtitle {
    font-size: 0.9rem !important;
    margin-bottom: 1.2rem;
  }

  .cta-button {
    padding: 0.7rem 1.5rem;
    font-size: 0.9rem;
    gap: 0.5rem;
  }

  .button-text {
    font-size: 0.9rem;
  }
}

/* RTL text direction; layout remains symmetric */
.top-bar, .main-nav { direction: rtl; }

/* TOP BAR ENDS HERE */


/* Hero Section - Reduce from 45vh to 35vh */
.hero-section {
    height: 55vh; /* Changed from 45vh */

    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    margin-bottom: 0;
    border-bottom: none;

}

.hero-background {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        rgba(0, 0, 0, 0.4),
        rgba(0, 0, 0, 0.6)
    ), url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1920 1080"><rect width="1920" height="1080" fill="%23667eea"/><circle cx="400" cy="300" r="150" fill="%23764ba2" opacity="0.3"/><circle cx="1500" cy="700" r="200" fill="%23d4af37" opacity="0.2"/></svg>');
    background-size: cover;
    background-position: center;
}

.hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
    color: white;
    max-width: 800px;
    padding: 0 2rem;
}

.hero-title {
    font-size: clamp(1.8rem, 4vw, 3rem);
    font-weight: 300;
    line-height: 1.3;
    margin-bottom: 1rem;
    opacity: 0;
    animation: fadeInUp 3s ease-out 0.5s forwards;
}

.hero-subtitle {
    font-size: clamp(1rem, 2vw, 1.25rem);
    font-weight: 400;
    opacity: 0.9;
    margin-bottom: 2rem;
    opacity: 0;
    animation: fadeInUp 3s ease-out 0.8s forwards;
}

/* כפתור ההירו – סנכרון עם הטקסטים */
.hero-section .cta-button {
  opacity: 0;
  transform: translateY(20px);
  animation: fadeInUp 3s ease-out 0.5s forwards; /* אותו טיימינג כמו ה-subtitle */
}

/* האנימציה קיימת אצלך, מצרף רק לוודא */
@keyframes fadeInUp {
  from { opacity: 0; transform: translateY(20px); }
  to   { opacity: 1; transform: translateY(0); }
}

/* Social Media Icons */

.social-links {
    display: flex;
    justify-content: center;
    gap: 1rem;
    opacity: 0;
    animation: fadeInUp 1s ease-out 1.1s forwards;
}

.social-link {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 70px;
    height: 70px;
    background: rgba(255, 255, 255, 0.1);
    border: 2px solid rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    color: white;
    text-decoration: none;
    transition: var(--transition);
    backdrop-filter: blur(10px);
}

.social-link:hover {
    background: var(--primary-color);
    border-color: var(--primary-color);
    transform: translateY(-3px);
}

.social-fixed {
    position: fixed;
    bottom: 20px;
    left: 20px;
    display: flex;
    flex-direction: row; /* הפיכה לאופקי */
    gap: 12px;
    z-index: 1000;
}

.social-fixed .social-link {
    color: white;
    font-size: 1.4em;
    background-color: rgba(0, 0, 0, 0.4);
    padding: 10px;
    border-radius: 50%;
    transition: background 0.3s ease;
    text-decoration: none;
}

.social-fixed .social-link:hover {
    background-color: #d4af37;
    color: black;
}

/* Category Grid - Add fixed height */
.category-grid {
    height: 45vh; /* Add this line */
    padding: 2rem 0; /* Reduce padding from 4rem to 2rem */
    display: flex;
    justify-content: center;
    align-items: center;
}

.grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(2, 1fr);
    gap: 2rem;
    width: 100%;
    height: 100%;
}

.category-card {
    background: rgba(255, 255, 255, 0.85); /* white with transparency */
    border-radius: 15px;
    padding: 1.5rem;
    text-align: center;
    box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
    backdrop-filter: blur(10px); /* optional for "frosted" effect */
    transition: var(--transition);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

.category-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, var(--primary-color), #f39c12);
    opacity: 0;
    transition: var(--transition);
    pointer-events: none; /* ✅ THIS is the fix */

}

.category-card:hover {
    transform: translateY(-5px);
    box-shadow: var(--shadow-heavy);
}

.category-card:hover::before {
    opacity: 0.1;
}

.card-icon {
    position: relative;
    z-index: 2;
    margin-bottom: 1rem;
}

.card-icon i {
    font-size: 2.5rem;
    color: var(--primary-color);
    transition: var(--transition);
}

.category-card:hover .card-icon i {
    color: var(--secondary-color);
    transform: scale(1.1);
}

.category-card h3 {
    position: relative;
    z-index: 2;
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: 0.5rem;
}

.category-card p {
    position: relative;
    z-index: 2;
    font-size: 0.9rem;
    color: var(--text-secondary);
    line-height: 1.4;
}

.cta-wrapper {
  display: inline-block;
}

.cta-area {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2vh 0;
  margin: 0;
  background: none;
}

.cta-button {
  display: inline-block;
  margin-top: 1.5rem;
  padding: 1rem 2.5rem;
  background: var(--primary-color);
  color: white;
  font-size: 1.25rem;
  font-weight: 600;
  border: none;
  border-radius: 40px;
  cursor: pointer;
  text-decoration: none;
  transition: background 0.3s ease, transform 0.2s ease;
  box-shadow: 0 6px 18px rgba(0, 0, 0, 0.15);
  min-width: 260px;
  position: relative;
  overflow: hidden;
}

.cta-button:hover {
  background: var(--primary-color);
  transform: translateY(-2px);
}

.cta-button:hover::before {
    left: 100%;
}

.cta-button::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.6s;
    border-radius: inherit;
}

/* Modal Styles */
.modal-overlay {
    position: fixed;
    inset: 0;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(10px);
    z-index: 10000;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    visibility: hidden;
    transition: var(--transition);
}

.modal-overlay.active {
    opacity: 1;
    visibility: visible;
}

.modal-container {
    background: white;
    border-radius: 20px;
    max-width: 800px;
    max-height: 80vh;
    width: 90%;
    position: relative;
    overflow: hidden;
    transform: scale(0.8);
    transition: var(--transition);
}

.modal-overlay.active .modal-container {
    transform: scale(1);
}

.modal-close {
    position: absolute;
    top: 1rem;
    left: 1rem;
    background: rgba(0, 0, 0, 0.1);
    border: none;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    cursor: pointer;
    z-index: 10;
    transition: var(--transition);
    display: flex;
    align-items: center;
    justify-content: center;
}

.modal-close:hover {
    background: rgba(0, 0, 0, 0.2);
}

.modal-content {
    padding: 3rem;
    overflow-y: auto;
    max-height: 80vh;
}

/* Onboarding Styles */
.onboarding-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.9);
    z-index: 2000;
    display: none;
    opacity: 0;
    transition: var(--transition);
}

.onboarding-overlay.active {
    opacity: 1;
}

.onboarding-container {
    height: 100vh;
    display: flex;
    flex-direction: column;
    background: white;
    max-width: 900px;
    margin: 0 auto;
    position: relative;
}

.onboarding-header {
    padding: 2rem;
    border-bottom: 1px solid #eee;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.onboarding-close {
    background: none;
    border: none;
    font-size: 1.5rem;
    cursor: pointer;
    color: var(--text-secondary);
    transition: var(--transition);
}

.onboarding-close:hover {
    color: var(--text-primary);
}

.progress-bar {
    flex: 1;
    height: 6px;
    background: #eee;
    border-radius: 3px;
    margin: 0 2rem;
    overflow: hidden;
}

.progress-fill {
    height: 100%;
    background: linear-gradient(90deg, var(--primary-color), #f39c12);
    border-radius: 3px;
    transition: width 0.5s ease;
    width: 14.28%;
}

.progress-text {
    font-size: 0.9rem;
    color: var(--text-secondary);
    font-weight: 500;
}

.onboarding-content {
    flex: 1;
    padding: 3rem;
    overflow-y: auto;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.onboarding-navigation {
    padding: 2rem;
    border-top: 1px solid #eee;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.nav-btn {
    background: var(--primary-color);
    color: white;
    border: none;
    padding: 0.75rem 2rem;
    border-radius: 25px;
    font-weight: 500;
    cursor: pointer;
    transition: var(--transition);
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.nav-btn:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-light);
}

.prev-btn {
    background: transparent;
    color: var(--text-secondary);
    border: 2px solid #eee;
}

.prev-btn:hover {
    background: #f8f9fa;
    color: var(--text-primary);
}

.question-container {
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  width: 100%;
  box-sizing: border-box;
}

.question-title {
    font-size: 1.8rem;
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: 1rem;
}

.question-subtitle {
    font-size: 1.1rem;
    color: var(--text-secondary);
    margin-bottom: 3rem;
    line-height: 1.5;
}

.options-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr); /* ⬅ עיקר השינוי כאן */
    gap: 1rem;
    margin-bottom: 2rem;
}

@media (max-width: 768px) {
    .options-grid {
        grid-template-columns: 1fr;
    }
}

.option-card {
  background: white;
  border: 2px solid rgba(212, 175, 55, 0.3);
  border-radius: 15px;
  padding: 1.5rem;
  text-align: right;
  cursor: pointer;
  transition: all 0.3s ease;
}

.option-card:hover {
  border-color: var(--primary-color);
  background: rgba(212, 175, 55, 0.05);
}


.option-card.selected {
    border-color: var(--primary-color);
    background: rgba(212, 175, 55, 0.1);
}

.option-title {
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: 0.5rem;
}

.option-description {
    font-size: 0.95rem;
    color: var(--text-secondary);
    line-height: 1.4;
}

/* Property Swipe Styles */
.swipe-interface {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, #ac6b11 0%, #f29136 100%);
    z-index: 5000;
}

.swipe-container {
    height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 2rem;
}

.swipe-header {
    text-align: center;
    color: white;
    margin-bottom: 3rem;
}

.swipe-header h2 {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.property-cards {
    position: relative;
    width: 350px;
    height: 500px;
    margin-bottom: 3rem;
}

.property-card {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: white;
    border-radius: 20px;
    overflow: hidden;
    box-shadow: var(--shadow-heavy);
    cursor: grab;
    transition: transform 0.3s ease;
}

.property-card.dragging {
    cursor: grabbing;
    transform: rotate(5deg);
}

.property-image {
    height: 60%;
    background: linear-gradient(45deg, #ddd, #f0f0f0);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 3rem;
    color: #999;
}

.property-info {
    padding: 1.5rem;
    height: 40%;
}

.property-title {
    font-size: 1.2rem;
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: 0.5rem;
}

.property-price {
    font-size: 1.4rem;
    font-weight: 700;
    color: var(--primary-color);
    margin-bottom: 0.5rem;
}

.property-location {
    font-size: 0.9rem;
    color: var(--text-secondary);
}

.swipe-actions {
    display: flex;
    gap: 2rem;
}

.swipe-btn {
    width: 70px;
    height: 70px;
    border-radius: 50%;
    border: none;
    cursor: pointer;
    transition: var(--transition);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
}

.dislike {
    background: #ff4757;
    color: white;
}

.like {
    background: #2ed573;
    color: white;
}

.swipe-btn:hover {
    transform: scale(1.1);
}

/* Animations */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideIn {
    from {
        transform: translateX(100%);
    }
    to {
        transform: translateX(0);
    }
}

@keyframes slideOut {
    from {
        transform: translateX(0);
    }
    to {
        transform: translateX(-100%);
    }
}

/* Responsive Design */
@media (max-width: 768px) {
    /* Keep navigation visible, don't hide it */
    .main-nav {
        display: block;
    }

    .grid {
        grid-template-columns: repeat(2, 1fr);
        grid-template-rows: repeat(3, 1fr);
        gap: 1rem;
    }

    .category-card {
        padding: 1rem;
    }

    .card-icon i {
        font-size: 2rem;
    }

    /* Social links mobile optimization */
    .social-fixed {
        right: 0.5rem;
        gap: 0.3rem;
    }

    .social-fixed .social-link {
        width: 45px;
        height: 45px;
        font-size: 1.2rem;
    }

    /* Contact bar mobile optimization */
    .contact-fixed {
        left: 0.5rem;
        padding: 0.5rem;
        font-size: 0.8rem;
        border-radius: 20px;
    }

    .contact-fixed .contact-btn {
        padding: 0.4rem 0.8rem;
        font-size: 0.8rem;
        border-radius: 15px;
    }

    /* Modal mobile optimization */
    .modal-container {
        width: 95%;
        max-height: 85vh;
        margin: 2.5vh auto;
        border-radius: 15px;
    }

    .modal-content {
        padding: 2rem 1.5rem;
        font-size: 0.9rem;
    }

    .modal-close {
        top: 0.5rem;
        left: 0.5rem;
        width: 35px;
        height: 35px;
    }
    }

    .hero-subtitle {
        font-size: 1rem;
    }

    .container {
        padding: 0 1rem;
    }
}

@media (max-width: 480px) {
    /* Top bar adjustments */
    .top-bar {
        height: 12vh;
    }

    .main-nav > ul.nav-grid {
        font-size: 0.7rem;
        column-gap: 0.3rem;
    }

    .logo { height: 2rem; }

    /* Hero section adjustments */
    .hero-section {
        padding: 0.5rem;
    }

    .hero-title {
        font-size: 1.3rem !important;
        line-height: 1.1;
        margin-bottom: 0.6rem;
    }

    .hero-subtitle {
        font-size: 0.8rem !important;
        margin-bottom: 1rem;
    }

    .cta-button {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
        border-radius: 25px;
    }

    /* Grid adjustments */
    .grid {
        grid-template-columns: 1fr;
        grid-template-rows: repeat(6, 1fr);
        gap: 0.5rem;
        padding: 0.5rem 0;
    }

    .category-card {
        padding: 0.75rem;
        border-radius: 10px;
    }

    .category-card h3 {
        font-size: 1rem;
        margin-bottom: 0.3rem;
    }

    .category-card p {
        font-size: 0.8rem;
    }

    .card-icon i {
        font-size: 1.8rem;
        margin-bottom: 0.5rem;
    }

    /* Property cards mobile */
    .property-cards {
        width: 280px;
        height: 400px;
    }

    /* Social links very small screens */
    .social-fixed .social-link {
        width: 40px;
        height: 40px;
        font-size: 1.1rem;
    }

    /* Contact bar very small screens */
    .contact-fixed {
        padding: 0.4rem;
        font-size: 0.7rem;
    }

    .contact-fixed .contact-btn {
        padding: 0.3rem 0.6rem;
        font-size: 0.7rem;
    }

    /* Modal very small screens */
    .modal-container {
        width: 98%;
        max-height: 90vh;
        margin: 1vh auto;
        border-radius: 10px;
    }

    .modal-content {
        padding: 1.5rem 1rem;
        font-size: 0.85rem;
    }

    /* Onboarding mobile optimization */
    .onboarding-container {
        max-width: 100%;
        margin: 0;
    }

    .onboarding-header {
        padding: 1rem;
        flex-wrap: wrap;
    }

    .onboarding-content {
        padding: 1.5rem 1rem;
    }

    .onboarding-navigation {
        padding: 1rem;
        flex-wrap: wrap;
        gap: 0.5rem;
    }

    /* Question container mobile */
    .question-container {
        max-width: 100%;
    }

    .question-title {
        font-size: 1.4rem;
        margin-bottom: 0.8rem;
    }

    .question-subtitle {
        font-size: 1rem;
        margin-bottom: 2rem;
    }

    .options-grid {
        gap: 0.75rem;
    }

    .option-card {
        padding: 1rem;
        font-size: 0.9rem;
    }

    .option-title {
        font-size: 1rem;
        margin-bottom: 0.4rem;
    }

    .option-description {
        font-size: 0.85rem;
    }

    /* Navigation buttons mobile */
    .nav-btn {
        padding: 0.6rem 1.5rem;
        font-size: 0.9rem;
        min-height: 44px;
    }
}


/* Contact Bar */
.contact-fixed {
    position: fixed;
    bottom: 20px;
    right: 20px;
    text-align: right;
    color: white;
    font-size: 1.4em;
    line-height: 1.6;
    background-color: none;
    padding: 10px 14px;
    border-radius: 12px;
    z-index: 1000;
    direction: ltr; /* force LTR for phone + email */
    background-color: none;
    transition: background 0.3s ease;
}

.contact-fixed p {
    margin: 0;
}

/* Executives Scroll */
.executive-carousel {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 500px; /* גובה שמספיק להכיל את הכרטיס הכי ארוך */
  position: relative;
}

.executive-slide {
  width: 100%;
  max-width: 600px;
  height: 600px; /* קובע גובה אחיד לכולם */
  background: white;
  padding: 2rem;
  border-radius: 15px;
  box-shadow: 0 0 20px rgba(0,0,0,0.1);
  display: none;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  text-align: center;
  box-sizing: border-box;
  overflow: hidden; /* מונע מהתוכן לפרוץ את הגובה */
  gap: 0.5rem; /* רווח מאוזן בין האלמנטים הפנימיים */

}

.executive-slide.active {
  display: flex;
}

.executive-description {
  margin-top: 0.5rem;
  overflow: hidden;
  text-overflow: ellipsis;
  padding: 0 1rem;
  line-height: 1.6;
  font-size: 1rem;
}

.executive-photo {
    width: 250px;
    height: 250px;
    border-radius: 50%;
    object-fit: cover;
    box-shadow: 0 4px 15px rgba(0,0,0,0.2);
    margin-bottom: 1rem;
}

.executive-name {
    font-weight: bold;
    color: var(--text-primary);
    margin-bottom: 0.5rem;
}

.carousel-nav {
    display: flex;
    justify-content: center;
    margin-top: 1rem;
}

.carousel-nav button {
    background-color: var(--primary-color);
    border: none;
    color: white;
    font-size: 1.5rem;
    padding: 0.5rem 1rem;
    margin: 0 0.5rem;
    border-radius: 10px;
    cursor: pointer;
}

.arrow-icon {
    margin-left: 10px; /* Adjust the value (e.g., 5px, 15px) to control the gap */
}

/* Bottom Bar - 10vh */
.bottom-bar {
    height: 10vh;
    backdrop-filter: none;
    background-color: none;
    border-top: none;
    position: fixed; /* קיבוע בתחתית */
    bottom: 0;
    left: 0;
    width: 100%;
    z-index: 100;
}

.bottom-bar .container {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center; /* מרכז את התפריט */
}

.footer-nav ul {
    list-style: none;
    display: flex;
    gap: 3rem; /* Spacing between categories */
    margin: 0;
    padding: 0;
}

.footer-nav a {
    text-decoration: none;
    color: white;
    font-size: 0.9rem;
    transition: color 0.3s ease;
}

.footer-nav a:hover {
    color: #c7af87; /* צבע בהובר */
}

/* === Accessibility Toolbar (A11Y) === */
:root { --a11y-w: 180px; --a11y-z: 9999; }

/* ה־Toolbar מגיב להגדלה גלובלית */
#a11y-toolbar {
  position: fixed;
  top: 20%;
  font-size: 1rem;
  line-height: 1.4;
  z-index: var(--a11y-z);
}
#a11y-toolbar.a11y-right { right: calc(-1 * var(--a11y-w)); transition: right .6s cubic-bezier(.23,1,.32,1); }
#a11y-toolbar.a11y-open.a11y-right { right: 0; }
#a11y-toolbar .a11y-toggle { position: absolute; right: var(--a11y-w); }

#a11y-toolbar .a11y-btn {
  display: inline-flex; align-items: center; justify-content: center; gap:.5rem;
  padding: 0.625rem; /* 10px */
  font-size: 1.25rem; /* היה 20px */
  background:#fff; border:1px solid #ddd; border-radius:10px; cursor:pointer;
  box-shadow: 0 0 10px rgba(0,0,0,.1);
}
#a11y-toolbar .a11y-btn:focus { outline: 0.1875rem solid #7aa7ff; outline-offset: 2px; } /* 3px */

#a11y-toolbar .a11y-panel {
  width: var(--a11y-w); border:1px solid #ddd; background:#fff; border-radius:10px; overflow:hidden;
  box-shadow: 0 8px 24px rgba(0,0,0,.1);
  font-size: 1rem;
}
#a11y-toolbar .a11y-title { font-weight:700; margin:0; padding:0.625rem 0.75rem 0; font-size:1rem; }
#a11y-toolbar .a11y-list { list-style:none; margin:0; padding:0.625rem 0; }
#a11y-toolbar .a11y-link {
  width:100%; text-align:right; background:none; border:0; cursor:pointer;
  padding:0.625rem 0.75rem; font-size:1em; /* היה 14px */
}
#a11y-toolbar .a11y-link:hover, #a11y-toolbar .a11y-link:focus { background:#f7f7f7; outline:none; }
#a11y-toolbar .a11y-reset { border-top:1px solid #eee; margin-top:0.375rem; }

/* מחלקות שנוספות לגוף הדף */
.a11y-grayscale { filter: grayscale(100%); }
.a11y-high-contrast * { background-image: none !important; box-shadow: none !important; }
.a11y-high-contrast, .a11y-high-contrast * { background:#000 !important; color:#fff !important; }
.a11y-negative, .a11y-negative * { background:#000 !important; color:yellow !important; }
.a11y-light-bg, .a11y-light-bg *:not(#a11y-toolbar) { background:#fff !important; color:#000 !important; }
.a11y-underline a, .a11y-underline a * { text-decoration: underline !important; }
.a11y-readable, .a11y-readable *:not(i):not(svg):not(code) { font-family: Verdana, Arial, Helvetica, sans-serif !important; }

/* הגדלת טקסט הדרגתית – מוחלת על html כדי להשפיע על rem */
html.a11y-font-120 { font-size: 120%; }
html.a11y-font-130 { font-size: 130%; }
html.a11y-font-140 { font-size: 140%; }
html.a11y-font-150 { font-size: 150%; }
html.a11y-font-160 { font-size: 160%; }
html.a11y-font-170 { font-size: 170%; }
html.a11y-font-180 { font-size: 180%; }
html.a11y-font-190 { font-size: 190%; }
html.a11y-font-200 { font-size: 200%; }

.sr-only {
  position: absolute; width:1px; height:1px; padding:0; margin:-1px; overflow:hidden; clip: rect(0,0,0,0); border:0;
}

@media (max-width: 768px) {
  #a11y-toolbar { top: 10%; }
}

/* ===== About Timeline ===== */
.about-timeline { --gap: 1.25rem; --card-w: 280px; --track: rgba(0,0,0,.08); }
.about-tl-header { text-align: center; margin-bottom: 1.25rem; }
.about-tl-header h2 { margin-bottom: .25rem; }
.about-tl-sub { color: var(--text-secondary); margin-top: 1rem;}

.about-tl-rail {
  position: relative;
  margin: 1rem 0 0.5rem;
  padding: 1.25rem 2.75rem; /* מקום לחיצים */
}

.about-tl-rail::before {
  content: "";
  position: absolute;
  right: 1rem; 
  left: 1rem;
  top: 50%;
  height: 4px;
  background: rgba(212,175,55,.35);
  transform: translateY(-50%);
  pointer-events: none;

  /* אפקט נשימה */
  animation: pulseLine 2.5s ease-in-out infinite;
}

/* אנימציית ה-pulse */
@keyframes pulseLine {
  0%, 100% {
    background: rgba(212,175,55,.35);
    box-shadow: 0 0 0 rgba(212,175,55,0);
  }
  50% {
    background: rgba(212,175,55,.55); /* קצת יותר מודגש באמצע */
    box-shadow: 0 0 10px rgba(212,175,55,.45);
  }
}

.about-tl-list {
  display: grid;
  grid-auto-flow: column;
  grid-auto-columns: minmax(var(--card-w), 1fr);
  gap: 5rem;
  overflow-x: auto;
  padding: .5rem 0;
  scroll-snap-type: x mandatory;
  scrollbar-width: thin;
}
.about-tl-list:focus { outline: none; }

.tl-item {
  scroll-snap-align: center;
  position: relative;
  background: #fff;
  border: 1px solid rgba(0,0,0,.06);
  border-radius: 14px;
  box-shadow: 0 8px 24px rgba(0,0,0,.06);
  overflow: hidden;
}

.tl-item.tl-highlight { border-color: rgba(212,175,55,.35); box-shadow: 0 10px 32px rgba(212,175,55,.18); }
.tl-cover { aspect-ratio: 4 / 3; background: #f4f4f4; }
.tl-cover img { width: 100%; height: 100%; object-fit: cover; display: block; }

.tl-meta { padding: .9rem 1rem 1.1rem; }
.tl-year { font-weight: 700; color: var(--text-secondary); }
.tl-badge {
  display: inline-block; padding: .15rem .5rem; border-radius: 999px;
  background: rgba(212,175,55,.12); color: var(--primary-color);
}
.tl-title { margin: .15rem 0 .35rem; font-weight: 700; color: var(--text-primary); }
.tl-desc { color: var(--text-secondary); line-height: 1.5; font-size: .95rem; }
.tl-note { font-style: italic; opacity: .85; }

.about-tl-nav {
  position: absolute; top: 50%; transform: translateY(-50%);
  width: 36px; height: 36px; border-radius: 50%;
  border: 0; cursor: pointer; z-index: 2;
  background: #fff; box-shadow: 0 4px 14px rgba(0,0,0,.12);
  display: grid; place-items: center; font-size: 1.25rem; line-height: 1;
  transition: transform .2s ease;
}
.about-tl-nav:hover { transform: translateY(-50%) scale(1.05); }
.about-tl-nav.prev { inset-inline-start: .5rem; }
.about-tl-nav.next { inset-inline-end: .5rem; }

.about-tl-foot { margin-top: 1rem; text-align: center; }

/* מובייל/טאבלט */
@media (max-width: 860px) {
  .about-timeline { --card-w: 240px; }
  .about-tl-rail { padding-inline: 2.25rem; }
  .about-tl-rail::before { right: 2.25rem; left: 2.25rem; }
}
@media (max-width: 520px) {
  .about-timeline { --card-w: 82vw; }
  .about-tl-rail { padding-inline: 1.75rem; }
  .about-tl-rail::before { right: 1.75rem; left: 1.75rem; }
}

/* ===== Services Timeline ===== */
.services-timeline { --gap: 1.25rem; --card-w: 280px; --track: rgba(0,0,0,.08); }
.services-tl-header { text-align: center; margin-bottom: 1.25rem; }
.services-tl-header h2 { margin-bottom: .25rem; }
.services-tl-sub { color: var(--text-secondary); margin-top: 1rem; }

.services-tl-rail {
  position: relative;
  margin: 1rem 0 0.5rem;
  padding: 1.25rem 2.75rem; /* מקום לחיצים */
}
.services-tl-rail::before {
  content: "";
  position: absolute;
  right: 1rem; 
  left: 1rem;
  top: 50%;
  height: 4px;
  background: rgba(212,175,55,.35);
  transform: translateY(-50%);
  pointer-events: none;

  /* אפקט נשימה */
  animation: pulseLine 2.5s ease-in-out infinite;
}

/* אנימציית ה-pulse */
@keyframes pulseLine {
  0%, 100% {
    background: rgba(212,175,55,.35);
    box-shadow: 0 0 0 rgba(212,175,55,0);
  }
  50% {
    background: rgba(212,175,55,.55); /* קצת יותר מודגש באמצע */
    box-shadow: 0 0 10px rgba(212,175,55,.45);
  }
}

.services-tl-list {
  display: grid;
  grid-auto-flow: column;
  grid-auto-columns: minmax(var(--card-w), 1fr);
  gap: 4rem;
  overflow-x: auto;
  padding: .5rem 0;
  scroll-snap-type: x mandatory;
  scrollbar-width: thin;
  -webkit-overflow-scrolling: touch;
}
.services-tl-list:focus { outline: none; }

.svc-item {
  scroll-snap-align: center;
  position: relative;
  background: #fff;
  border: 1px solid rgba(0,0,0,.06);
  border-radius: 14px;
  box-shadow: 0 8px 24px rgba(0,0,0,.06);
  overflow: hidden;
}

.svc-cover { aspect-ratio: 4 / 3; background: #f4f4f4; }
.svc-cover img { width: 100%; height: 100%; object-fit: cover; display: block; }

.svc-meta { padding: .9rem 1rem 1.1rem; }
.svc-year { font-weight: 700; color: var(--text-secondary); }
.svc-title { margin: .15rem 0 .35rem; font-weight: 700; color: var(--text-primary); margin-bottom:0.6rem; }
.svc-desc { color: var(--text-secondary); line-height: 1.5; font-size: .95rem; }
.svc-note { font-style: italic; opacity: .85; }

.services-tl-nav {
  position: absolute; top: 50%; transform: translateY(-50%);
  width: 36px; height: 36px; border-radius: 50%;
  border: 0; cursor: pointer; z-index: 2;
  background: #fff; box-shadow: 0 4px 14px rgba(0,0,0,.12);
  display: grid; place-items: center; font-size: 1.25rem; line-height: 1;
  transition: transform .2s ease;
}
.services-tl-nav:hover { transform: translateY(-50%) scale(1.05); }
.services-tl-nav.prev { inset-inline-start: .5rem; }
.services-tl-nav.next { inset-inline-end: .5rem; }

.services-tl-foot { margin-top: 1rem; text-align: center; }


.team-bullet{
  position:absolute; top:1rem; inset-inline-start:1rem; /* RTL ידידותי */
  width:36px; height:36px; border-radius:50%; border:0; background:transparent;
  padding:0; cursor:pointer; display:grid; place-items:center;
}
.team-bullet .dot{
  width:12px; height:12px; border-radius:50%;
  background:rgba(212,175,55,.9);
  box-shadow:0 0 0 4px rgba(212,175,55,.25);
  transition:transform .15s ease, box-shadow .15s ease;
}
.team-bullet:hover .dot, .team-bullet:focus-visible .dot{
  transform:scale(1.1); box-shadow:0 0 0 6px rgba(212,175,55,.3);
}
